const questions = [
  { 
    question: 'What is React?',
    choices: ['Library', 'Framework', 'Language', 'Platform'],
    answer: 'Library'
  },
  { 
    question: 'Which company developed React?',
    choices: ['Google', 'Facebook', 'Twitter', 'Microsoft'],
    answer: 'Facebook'
  },
  { 
    question: 'What is JSX?',
    choices: ['A programming language', 'A compiler', 'A syntax extension for JavaScript', 'A markup syntax'],
    answer: 'A syntax extension for JavaScript'
  },
  { 
    question: 'Where do you make Ajax requests in React?',
    choices: ['componentDidMount', 'componentWillMount', 'the render method', 'componentDidUpdate'],
    answer: 'componentDidMount'
  },
  { 
    question: 'What is Redux?',
    choices: ['A React plugin', 'A state management library', 'A component library', 'An animation library'],
    answer: 'A state management library'
  },
  { 
    question: 'What does the useState hook do?',
    choices: ['Connects to a database', 'Creates a new state variable', 'Updates the document title', 'Handles events'],
    answer: 'Creates a new state variable'
  },
  { 
    question: 'What is a higher-order component?',
    choices: ['A function that takes a component and returns a new component', 'A component that renders another component', 'A class that inherits from another class', 'A function that handles routing'],
    answer: 'A function that takes a component and returns a new component'
  },
  { 
    question: 'What is a key in React lists?',
    choices: ['A way to identify list items', 'A type of prop', 'A global identifier', 'A component method'],
    answer: 'A way to identify list items'
  },
  { 
    question: 'What is the virtual DOM?',
    choices: ['A DOM manipulation library', 'A copy of the real DOM in memory', 'A type of component', 'An event handling system'],
    answer: 'A copy of the real DOM in memory'
  },
  { 
    question: 'How do you pass a variable to a component?',
    choices: ['Using state', 'As a prop', 'With an event', 'Through context'],
    answer: 'As a prop'
  }
];

export default questions;
